/* scrollRight transition plugin for Cycle2;  version:20130331 ©webchain(不惑仔) http://boohover.pixnet.net/ */
(function(c){
	c.fn.cycle.transitions.scrollRight={
		before:function(a,b,c,d){a.API.stackSlides(b,c,d);
		b=a.container.css("overflow","hidden").width();
		a.cssBefore={top:0,left:-b,opacity:1,display:"block"};
		a.animIn={left:0};
		a.animOut={left:b}
		}}
		})(jQuery);